// src/components/Dashboard/Transactions.js
import React, { useEffect, useState } from 'react';
import API from '../../services/api';

const emptyForm = {
  title: '',
  amount: '',
  type: 'EXPENSE',
  category: 'OTHER',
  date: '',
};

const Transactions = () => {
  const [transactions, setTransactions] = useState([]);
  const [form, setForm] = useState(emptyForm);
  const [editingId, setEditingId] = useState(null);   // ← track row in edit mode
  const [editForm, setEditForm] = useState(emptyForm);
  const [error, setError] = useState('');

  /* -------- fetch all -------- */
  const fetchTransactions = async () => {
    try {
      const res = await API.get('/transactions');
      setTransactions(res.data);
    } catch (err) {
      console.error('Fetch failed:', err);
      setError('Could not load transactions');
    }
  };

  useEffect(() => {
    fetchTransactions();
  }, []);

  /* -------- add new -------- */
  const handleAdd = async (e) => {
    e.preventDefault();
    try {
      await API.post('/transactions', form);
      setForm(emptyForm);
      fetchTransactions();
    } catch (err) {
      console.error('Add failed:', err);
    }
  };

  /* -------- delete -------- */
  const handleDelete = async (id) => {
    try {
      await API.delete(`/transactions/${id}`);
      setTransactions((prev) => prev.filter((tx) => tx.id !== id));
    } catch (err) {
      console.error('Delete failed:', err);
    }
  };

  /* -------- begin edit -------- */
  const startEdit = (tx) => {
    setEditingId(tx.id);
    setEditForm({
      title: tx.title,
      amount: tx.amount,
      type: tx.type,
      category: tx.category,
      date: tx.date,
    });
  };

  /* -------- save edit -------- */
  const saveEdit = async (id) => {
    try {
      await API.put(`/transactions/${id}`, editForm);
      setEditingId(null);
      fetchTransactions();
    } catch (err) {
      console.error('Update failed:', err);
    }
  };

  /* -------- cancel edit -------- */
  const cancelEdit = () => {
    setEditingId(null);
  };

  /* -------- input helpers -------- */
  const onChangeAdd   = (e) => setForm({ ...form, [e.target.name]: e.target.value });
  const onChangeEdit  = (e) => setEditForm({ ...editForm, [e.target.name]: e.target.value });

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Transactions</h2>

      {/* Add Transaction */}
      <form onSubmit={handleAdd} style={{ marginBottom: 20 }}>
        <input name="title"      value={form.title}      onChange={onChangeAdd}  placeholder="Title" required />
        <input name="amount"     value={form.amount}     onChange={onChangeAdd}  type="number" placeholder="Amount" required />
        <select name="type"      value={form.type}       onChange={onChangeAdd}>
          <option value="INCOME">INCOME</option>
          <option value="EXPENSE">EXPENSE</option>
        </select>
        <select name="category"  value={form.category}   onChange={onChangeAdd}>
          <option value="FOOD">FOOD</option>
          <option value="UTILITIES">UTILITIES</option>
          <option value="ENTERTAINMENT">ENTERTAINMENT</option>
          <option value="OTHER">OTHER</option>
        </select>
        <input name="date" type="date" value={form.date} onChange={onChangeAdd} required />
        <button type="submit">Add</button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* List */}
      <ul>
        {transactions.map((tx) =>
          editingId === tx.id ? (
            /* --- Edit Row --- */
            <li key={tx.id}>
              <input name="date"     type="date"  value={editForm.date}     onChange={onChangeEdit} />
              <select name="type"    value={editForm.type}                onChange={onChangeEdit}>
                <option value="INCOME">INCOME</option>
                <option value="EXPENSE">EXPENSE</option>
              </select>
              <input name="amount"   type="number" value={editForm.amount} onChange={onChangeEdit} />
              <input name="category"             value={editForm.category} onChange={onChangeEdit} />
              <input name="title"                value={editForm.title}    onChange={onChangeEdit} />
              <button onClick={() => saveEdit(tx.id)}>Save</button>
              <button onClick={cancelEdit}>Cancel</button>
            </li>
          ) : (
            /* --- Display Row --- */
            <li key={tx.id}>
              {tx.date} | {tx.type} | ${tx.amount} | {tx.category} | {tx.title}
              &nbsp;
              <button onClick={() => startEdit(tx)}>Edit</button>
              <button onClick={() => handleDelete(tx.id)}>Delete</button>
            </li>
          )
        )}
      </ul>
    </div>
  );
};

export default Transactions;
