import React, { useEffect, useState } from 'react';
import API from '../../services/api';

const Budget = () => {
  const [month, setMonth] = useState('');
  const [limit, setLimit] = useState('');
  const [budget, setBudget] = useState(null);
  const [error, setError] = useState('');

  const fetchBudget = async () => {
    try {
      const res = await API.get(`/budget?month=${month}`);
      setBudget(res.data);
    } catch (err) {
      setError('No budget found for this month.');
      setBudget(null);
    }
  };

  const handleSubmit = async () => {
    try {
      const payload = { month, limit: parseFloat(limit) };
      const res = await API.post('/budget', payload);
      setBudget(res.data);
      setError('');
    } catch (err) {
      setError('Error saving budget');
    }
  };

  return (
    <div>
      <h2>Monthly Budget</h2>
      <input
        type="month"
        value={month}
        onChange={(e) => setMonth(e.target.value)}
        required
      />
      <input
        type="number"
        placeholder="Budget Limit"
        value={limit}
        onChange={(e) => setLimit(e.target.value)}
        required
      />
      <button onClick={handleSubmit}>Save Budget</button>
      <button onClick={fetchBudget}>Load Budget</button>

      {budget && (
        <div>
          <p>Month: {budget.month}</p>
          <p>Limit: ${budget.limit}</p>
        </div>
      )}

      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default Budget;
