import React, { useState } from 'react';
import API from '../../services/api';

const Dashboard = () => {
  const [month, setMonth] = useState('');
  const [summary, setSummary] = useState(null);
  const [error, setError] = useState('');

  const loadSummary = async () => {
    try {
      const res = await API.get(`/transactions/summary?month=${month}`);
      setSummary(res.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch summary');
      setSummary(null);
    }
  };

  return (
    <div>
      <h2>Monthly Dashboard</h2>
      <input
        type="month"
        value={month}
        onChange={(e) => setMonth(e.target.value)}
      />
      <button onClick={loadSummary}>Load Summary</button>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {summary && (
        <div>
          <p><strong>Month:</strong> {summary.month}</p>
          <p><strong>Total Income:</strong> ${summary.totalIncome}</p>
          <p><strong>Total Expense:</strong> ${summary.totalExpense}</p>
          <p><strong>Budget Limit:</strong> ${summary.budgetLimit}</p>
          <p><strong>Remaining:</strong> ${summary.remaining}</p>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
